QUERY.SQL:
select IDEN_MED_GRUPPO,
				cursor(
					select U.IDEN_PER, U.USERNAME, cursor(
						select P.NOME, P.VALORE
						from CONFIG$PARAMETRI P WHERE P.ID_GRUPPO=U.USERNAME
						and P.NOME in('INVIO_RR_SAL', 'SHOW_FARMACO_ORIGINALE')
						and P.SITO in ('MMG','ALL')
						and P.ATTIVO='S'
					) PARAMETRI
					FROM CONFIG$UTENTI U
					where U.IDEN_PER in (
						select column_VALUE from table(split(IDEN_MED_GRUPPO))
					)
					and U.SITO in ('MMG','ALL') and U.ATTIVO='S'
				) UTENTI
				from (
					select string_agg(distinct COLUMN_VALUE) as IDEN_MED_GRUPPO
					from table(split(get_gruppo_med(:iden_utente)))
				)

begin
    update SCADENZARIO set ATTIVO=:v_attivo where IDEN = :n_iden;
    commit;
end;

SELECT distinct IDEN_CDC as IDEN, COD_CDC AS CODICE, DESCRIZIONE_CDC AS GRUPPO FROM VIEW_UTENTI_CDC WHERE 
(USERNAME like :utente and :tipo_ricerca = 'UTENTE')
OR ( descrizione_cdc like :gruppo||'%' and :tipo_ricerca = 'GRUPPO'){#ORDER#}

SELECT * FROM (
/*Note diario senza schede associate, comprese gravidanze*/
SELECT coalesce(c.IDEN_GRAVIDANZA,c.IDEN) as IDEN, c.IDEN as iden_nota, c.IDEN_PROBLEMA, c.ute_ins, c.IDEN_MED, c.DATA AS DATA_ISO, c.data_ins AS data_inserimento, c.oscurato, (SELECT descr FROM DATI$PERSONALE WHERE iden = c.IDEN_MED) AS medico, c.tipo AS SCHEDA, c.TIPO TIPO_NOTE, c.note AS nota FROM mmg_diario c WHERE c.data BETWEEN :data_da AND :data_a AND c.iden_anag = :iden_anag AND ( c.iden_problema = :iden_problema OR :iden_problema IS NULL OR c.iden_problema IN (SELECT iden FROM mmg_problemi WHERE iden_parent = :iden_problema) ) AND c.IDEN_SCHEDA_XML is null AND c.deleted = 'N' AND VUA(c.IDEN_ANAG, :iden_utente, c.IDEN_MED, c.data, 'ASSISTITO,SOSTITUTO,GRUPPO,SOSTITUTO_GRUPPO',c.oscurato,c.UTE_INS)=1
UNION ALL
/*Schede XML considerate da TAB_CONFIGURA_XSLT*/
SELECT * FROM (SELECT x.IDEN, null as iden_nota, x.IDEN_PROBLEMA, x.ute_ins, x.IDEN_MED, x.DATA DATA_ISO, TO_CHAR(x.DATA_INS,'YYYYMMDD') DATA_INSERIMENTO, x.oscurato, (SELECT descr FROM DATI$PERSONALE WHERE iden = x.IDEN_MED) AS medico, SCHEDA, CASE WHEN x.scheda like 'BILANCIO_SALUTE%' THEN 'BILANCI_DI_SALUTE' WHEN x.scheda IN ('ANAMNESI','MMG_ANAMNESI_PRE_NEONATALE','MMG_VISITE','PLS_VISITE','VISITA' ) THEN 'VISITE' WHEN x.scheda IN ('RILEVAZIONI') THEN 'RILEVAZIONI' ELSE 'NOTE' END TIPO_NOTE, f_GetXML_XSLTed(scheda,:iden_utente,:iden_gruppo,:tipo_med,contenuto_xml) AS NOTA FROM mmg_schede_xml X WHERE iden_anag =:iden_anag AND VUA(IDEN_ANAG, :iden_utente, x.iden_med, DATA, 'ASSISTITO,SOSTITUTO,GRUPPO,SOSTITUTO_GRUPPO', x.oscurato, x.UTE_INS)=1 AND DELETED = 'N') WHERE NOTA IS NOT NULL AND DATA_ISO BETWEEN :data_da AND :data_a AND ( iden_problema = :iden_problema OR :iden_problema IS NULL OR iden_problema IN (SELECT iden FROM mmg_problemi WHERE iden_parent = :iden_problema) )
UNION ALL
/*Rilevazioni*/
SELECT NULL AS IDEN, null as iden_nota, IDEN_PROBLEMA, ute_ins, IDEN_MED, DATA AS DATA_ISO, DATA AS DATA_INSERIMENTO, 'N' oscurato, (SELECT descr FROM DATI$PERSONALE WHERE iden = iden_med) AS medico, 'RILEVAZIONI' AS SCHEDA, 'RILEVAZIONI' AS tipo_note, f_GetXML_XSLTed( 'RILEVAZIONI', :iden_utente, :iden_gruppo, :tipo_med, xmlelement("RILEVAZIONE", xmlelement("DATA",DATA), xmlelement("IDEN_ANAG",IDEN_ANAG), xmlelement("PESO",MAX(peso)), xmlelement("ALTEZZA",MAX(altezza)), xmlelement("BMI",MAX(bmi)), xmlelement("DIAMETRO_FONTANELLA",MAX(DIAMETRO_FONTANELLA)), xmlelement("CIRCONF_CRANICA",MAX(CIRCONF_CRANICA)) ) ) AS NOTA FROM (SELECT iden_anag, iden_med, data, iden_problema, altezza, peso, bmi, NULL AS DIAMETRO_FONTANELLA, NULL AS CIRCONF_CRANICA, ute_ins FROM mmg_accert_rilevazioni WHERE tipo_rilevazione = 'PESO_ALTEZZA' AND deleted ='N' UNION ALL SELECT iden_anag, iden_med, data, iden_problema, NULL AS altezza, NULL AS peso, NULL AS bmi, valore_1 AS DIAMETRO_FONTANELLA, NULL AS CIRCONF_CRANICA, ute_ins FROM mmg_accert_rilevazioni WHERE tipo_rilevazione = 'DIAMETRO_FONTANELLA' AND deleted ='N' UNION ALL SELECT iden_anag, iden_med, data, iden_problema, NULL AS altezza, NULL AS peso, NULL AS bmi, NULL AS DIAMETRO_FONTANELLA, valore_1 AS CIRCONF_CRANICA, ute_ins FROM mmg_accert_rilevazioni WHERE tipo_rilevazione = 'CIRCONF_CRANICA' AND deleted ='N' ) WHERE iden_anag = :iden_anag AND data BETWEEN :data_da AND :data_a AND ( iden_problema = :iden_problema OR :iden_problema IS NULL OR iden_problema IN (SELECT iden FROM mmg_problemi WHERE iden_parent = :iden_problema) ) AND VUA(IDEN_ANAG, :iden_utente, IDEN_MED, data, 'ASSISTITO,SOSTITUTO,GRUPPO,SOSTITUTO_GRUPPO','N',UTE_INS)=1 GROUP BY data, iden_anag, iden_med, iden_problema, UTE_INS ) WHERE 'ALL' = :tipo_nota OR TIPO_NOTE IN (SELECT column_value FROM TABLE(split(:tipo_nota))
) {#ORDER#}

select IDEN, UTE_INS, IDEN_MED, DATA_INTERVENTO, DESCRIZIONE, DESCRIZIONE_ICD9,
				CODICE_ICD9,OSCURATO from MMG_INTERVENTI where IDEN_ANAG = :iden_anag and
				deleted = 'N' and (oscurato != 'S' or UTE_INS = :iden_per ) {#ORDER#}

select DESCRIZIONE, DESCR_PERS, CODICE FROM MMG_VACCINI
				where IDEN_ANAG = :iden_anag and deleted = 'N' AND ATTIVO = 'S'
				{#ORDER#}

select IDEN, DATA DATA_SCREENING, UTE_INS, IDEN_MED, TIPO_SCREENING,
				ESITO_SCREENING, OSCURATO from MMG_SCREENING WHERE IDEN_ANAG =:iden_anag
				and DELETED = 'N' and VUA(IDEN_ANAG, :ute_ins, UTE_INS, DATA,'ASSISTITO,SOSTITUTO,GRUPPO,SOSTITUTO_GRUPPO',OSCURATO) = 1 {#ORDER#}

select IDEN, PESO, ALTEZZA, BMI, DATA from MMG_ACCERT_RILEVAZIONI 
				where IDEN_ANAG = :iden_anag and IDEN_MED = :iden_med and IDEN_PROBLEMA =:iden_problema 
				AND TIPO_RILEVAZIONE = 'PESO_ALTEZZA' AND DELETED = 'N' {#ORDER#}

select IDEN, VALORE_1 VALORE_REGISTRATO, DATA, TIPO_RILEVAZIONE from MMG_ACCERT_RILEVAZIONI 
				where IDEN_ANAG = :iden_anag and IDEN_MED = :iden_med and IDEN_PROBLEMA =:iden_problema 
				AND TIPO_RILEVAZIONE IN('ATTIVITA_FISICA', 'CIRCONF_ADDOMINALE', 'INR', 'TAO', 'GLICEMIA', 'SATURAZIONE OSSIGENO' ) AND DELETED = 'N' {#ORDER#}

select IDEN, VALORE TEMPERATURA, DATA from MMG_ACCERT_RILEVAZIONI 
				where IDEN_ANAG = :iden_anag and IDEN_MED = :iden_med and IDEN_PROBLEMA =:iden_problema 
				AND TIPO_RILEVAZIONE = 'TEMPERATURA' AND DELETED = 'N' {#ORDER#}

select IDEN, CODICE as VALUE, DESCRIZIONE as DESCR from
				VIEW_MMG_ICD9 where CODICE like :descr ||'%' AND tipo =
				'DIAGNOSI' AND ATTIVO = 'S'{#ORDER#}

select CODICE_PRODOTTO value,descrizione descr from
				FARMADATI$VM_PRODOTTI_RICERCA where COMMERCIO ='S' AND 
				CONTAINS(DESCRIZIONE, decode(:descr,'','NOTDEFINED','%','NOTDEFINED', :descr)||'%', 1) > 0 {#ORDER#}

select iden, cod_dec as VALUE, descrizione as DESCR from
				CC$ICD where COD_DEC like :descr ||'%' AND tipo = 'PROCEDURE'
				and ATTIVO = 'S' {#ORDER#}

select iden, cod_dec as VALUE, descrizione as DESCR from
				CC$ICD where descrizione like '%'|| :descr ||'%' AND tipo =
				'PROCEDURE' and ATTIVO = 'S' {#ORDER#}

SELECT CAP, IDEN , COMUNE AS DESCR, PROVINCIA, COD_COM, ATTIVO,
				CODICE_ISTAT AS VALUE, COD_DEC FROM VIEW_COMUNI WHERE ATTIVO =
				'S' AND COMUNE LIKE :descr ||'%'{#ORDER#}

select codice value,descrizione descr from mmg_pip WHERE
				ATTIVO='S' AND DESCRIZIONE like '%'||:descr||'%' AND TIPO_MED =
				'MMG' {#ORDER#}

select codice value,descrizione descr from mmg_pip WHERE
				ATTIVO='S' AND DESCRIZIONE like '%'||:descr||'%' AND TIPO_MED =
				'PLS' {#ORDER#}

SELECT CODICE VALUE, DESCRIZIONE DESCR FROM MMG_VACCINI WHERE
				ATTIVO = 'S' AND UPPER( DESCRIZIONE ) LIKE UPPER( :descr||'%' )
				{#ORDER#}

select null as descr, null as value from dual union all select
				/*+first_rows(100)*/ descrizione as descr,iden as value from
				mmg_codifiche where tipo_dato=:tipo_dato and attivo = 'S' order by descr desc

select null as descr, null as value from dual union all select
				/*+first_rows(100)*/ descrizione as descr,iden as value from
				mmg_codifiche where tipo_dato=:tipo_dato and attivo = 'S' order by descr desc

select null as descr, null as value from dual union all select
				/*+first_rows(100)*/ descrizione as descr,iden as value from
				mmg_codifiche where tipo_dato=:tipo_dato and attivo = 'S' order by descr desc

select null as descr, null as value from dual union all select
				/*+first_rows(100)*/ descrizione as descr,iden as value from
				mmg_codifiche where tipo_dato=:tipo_dato and attivo = 'S' order by descr desc

select null as descr, null as value from dual union all select
				/*+first_rows(100)*/ descrizione as descr,iden as value from
				mmg_codifiche where tipo_dato=:tipo_dato and attivo = 'S' order by descr desc

select null as descr, null as value from dual union all select
				/*+first_rows(100)*/ descrizione as descr,iden as value from
				mmg_codifiche where tipo_dato=:tipo_dato and attivo = 'S' order by descr desc

select null as descr, null as value from dual union all select
				/*+first_rows(100)*/ descrizione as descr,iden as value from
				mmg_codifiche where tipo_dato=:tipo_dato and attivo = 'S' order by descr desc

select DESCRIZIONE_ICD9 || ' ' ||TO_CHAR(TO_DATE(DATA_INTERVENTO,'YYYYMMDD'),'DD/MM/YYYY') INTERVENTI
				from MMG_INTERVENTI where IDEN_ANAG = :iden_anag and
				deleted = 'N' {#ORDER#}

select substr(extractvalue(CONTENUTO_XML, '//CAMPO[@COL="COMUNE"]/text()'),1,1000) COMUNE 
        		FROM MMG_SCHEDE_XML WHERE IDEN_ANAG = :iden_anag and SCHEDA = 'MMG_MODULO_MALATTIA_INFETTIVA_NOT' AND UTE_INS = :ute_ins and DATA_INS = (select max(DATA_INS)
				from MMG_SCHEDE_XML WHERE IDEN_ANAG = :iden_anag and SCHEDA = 'MMG_MODULO_MALATTIA_INFETTIVA_NOT' AND UTE_INS = :ute_ins)
